        let memesArray = memesData.data.memes;
        const ranNum = Math.floor(Math.random * memesArray.length());
        memeImage = memesArray[ranNum].url;
    }

    return(
        <main>
            <div className="form">
                <input 
                    type="text" 
                    className="form-input" 
                    placeholder="Top text"/>
                <input 
                    type="text" 
                    className="form-input" 
                    placeholder="Bottom text"/>
                <button onClick={getImage} type="button">Get a new meme image üñºÔ∏è</button>
                <img src={memeImage}/>
            </div>
        </main>
    )
}
export default Meme;
